import React, { Component } from "react";
// import {BrowserRouter as Router, Route, Link, NavLink} from 'react-router-dom';
// import Home from './components/Home'
import { OpenWeatherMapHelper } from 'openweathermap-node';
import ZipCode from "./components/ZipCode";
let APIKEY = 'ed4d2c1099d8312f09709e182a9f0e3d';
//
// //http://api.openweathermap.org/data/2.5/forecast?id=524901&APPID={APIKEY}
//
// let URL = `https://api.openweathermap.org/data/2.5/`;
// const DEFAULT_QUERY = `10128`;
const helper = new OpenWeatherMapHelper(
    {
        APPID: APIKEY,
        units: "imperial"
    }
);

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      zipcode: "",
      //url: `${URL}weather/zip=${DEFAULT_QUERY}&appid=${APIKEY}`,
      weather: []
    };
  }

  handleZipcodeSubmit = (e) => {
    e.preventDefault()
    this.props.onSubmitZipcode(this.state.zipcode);
    this.setState({
      zipcode: ""
    });
  };

  handleZipcodeUpdate = e => {
    // e.preventDefault()
    let zip = e.target.value;
    let url = this.setWeatherUrl(e.target.value)
    this.setState({
      zipcode: zip,
      url
    });



  };

  // setWeatherUrl = (q) => {
  //   return `${URL}forecast/zip=${q}&APPID=${APIKEY}`
  // }

  componentDidMount() {
    helper.getCurrentWeatherByZipCode("90003", (err, currentWeather) => {
      if(err){
          console.log(err);
      }
      else{
          console.log(currentWeather);
      }
    });
    // fetch(this.state.url)
    // .then(res => res.json())
    // .then(result => console.log(result))
    // this.setWeatherUrl(this.state.zipcode)
  }

  render() {
    // console.log(this.state.url)
    // this.setWeatherUrl(this.state.zipcode)
    return (
      <ZipCode
        zipcode={this.state.zipcode}
        handleZipcodeUpdate={this.handleZipcodeUpdate}
        handleZipcodeSubmit={this.handleZipcodeSubmit}
      />
    );
  }
}
