import React, { Component } from "react";
// import {BrowserRouter as Router, Route, Link, NavLink} from 'react-router-dom';
// import Home from './components/Home'
import ZipCode from "./components/ZipCode";
let APIKEY = "1ba52e2d0b10783cca990ba43e51f792";

//http://api.openweathermap.org/data/2.5/forecast?id=524901&APPID={APIKEY}

let URL = `http://api.openweathermap.org/data/2.5/`;

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      zipcode: "",
      weather: []
    };
  }

  handleZipcodeSubmit = (e) => {
    e.preventDefault()
    this.props.onSubmitZipcode(this.state.zipcode);
    this.setState({
      zipcode: ""
    });
  };

  handleZipcodeUpdate = e => {
    // e.preventDefault()
    let zip = e.target.value;
    let url = this.setWeatherUrl(e.target.value)
    this.setState({
      zipcode: zip,
      url
    });



  };

  setWeatherUrl = (q) => {
    return `${URL}weather/zip=${q}`
  }
  componentDidMount() {
    console.log(this.state.url)
    // this.setWeatherUrl(this.state.zipcode)
  }

  render() {
    this.setWeatherUrl(this.state.zipcode)
    return (
      <ZipCode
        zipcode={this.state.zipcode}
        handleZipcodeUpdate={this.handleZipcodeUpdate}
        handleZipcodeSubmit={this.handleZipcodeSubmit}
        handleUpdateUrl={this.setWeatherUrl(this.state.zipcode)}
      />
    );
  }
}
