import React from "react";
const City = (props) => {

    handleCitySubmit = () => {
      this.props.handleCitySubmit(this.state.zipcode);
      this.setState({
        zipcode: ""
      });
    };

    handleCitySubmit = e => {
      let zip = e.target.value;

      this.setState({
        zipcode: zip
      });
    };

    return (
      <div className="container is-fullwidth">
        <div className="field">
          <p className="title label"></p>
          <div className="control">
            <input
              className="input"
              onChange={props.handleCityUpdate}
              type="text"
              value={props.city}
            />
          </div>
        </div>
        <div className="field">
          <button
            className="button is-large is-primary"
            onClick={props.handleCitySubmit}
            onSubmit={props.handleCitySubmit}
          >
            Get Weather!
          </button>
        </div>
      </div>
    );
}

export default City;
