import React, { Component } from "react";
import {Link} from 'react-router-dom'
// RAILSAPI = 'http://localhost:3000/api/v1/users/'

export default class Home extends Component {
  // we need an input filed to create a new user.
  // this is where we will post that user to the backend.
  // state will capture the user input.
  constructor(props) {
    super(props);

    this.state = {
      usernameInput: "",
      currentUser: null,
      response: []
    };
  }

  postApiCreateUser = event => {
    console.log("state", this.state.usernameInput);
    event.preventDefault();
    fetch("http://localhost:9000/api/v1/users/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        username: this.state.usernameInput
      })
    }).then(res => res.json())
    .then(response => {
      debugger
      this.setState({
        response: response.errors,
      },() => console.log(this.state))
      // console.log('Error:', response)
    })
    // debugger
    document.querySelector("#userForm").reset();
  };

  fetchUserApi = () => {
    if(this.state.response !== []) {
      fetch('http://localhost:9000/api/v1/users/')
      .then(res => res.json())
      .then(userData => {
        this.setState({
          currentUser: userData.id
        })
      })
    }
  }
    //fetch users if there are no errors on the post,
    //fetch to get that user. set the state of the current user from the fetch.


  // componentDidMount() {
  //   this.postFetchUserApi()
  // }

  handleChangeUsername = e => {
    // console.log(e.target.value);
    this.setState({
      [e.target.id]: e.target.value
    });
  };

  render() {
    this.fetchUserApi()
    return (
      <section className="hero is-primary is-fullheight">
        <div className="hero-body">
          <div className="container">
            <div className="columns is-centered">
              <div className="column">
                <div className="column is-6-tablet is-12-desktop is-12-widescreen">
                  <h1>{this.state.response}</h1>
                  <form className="box" id="userForm" onSubmit={this.postApiCreateUser}>
                    <div className="field">
                      <label htmlFor="username">User Name: </label>
                      <input
                        className="input"
                        onChange={this.handleChangeUsername}
                        id="usernameInput"
                        type="text"
                        name="name"
                      />
                      <button className="button is-success" type="submit" value="Log In"><Link to="/search">Log in</Link></button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // <Router>
  //   <div className="App">
  //
  //     <nav>
  //       <NavLink exact to="/" activeClassName="selected">Home</NavLink>
  //     </nav>
  //     <Route exact path="/" render={() => <Home />}/>
  //
  //   </div>
  // </Router>
}
